def verdict(text,freq_pos,freq_neg):

    number_of_articles = 13 #dev only

    word = re.compile("[^\.,\?!;:]\S+?(?= |\.|,|\?|!|;|:)")  #duplicated from naive_bayes.count_tokens()
    words = re.findall(word,text)

    token_probs = []
    for word in words:
        word = word.strip() #duplicated from naive_bayes.count_tokens()
        #print "|"+word+"|",

        try:
            fp = freq_pos[word] 
            #print "positive: ",fp
        except KeyError:
            fp = 0
            #print fp,

        try:
            fn = freq_neg[word]
            #print "negative: ",fn
        except KeyError:
            fn = 0
            #print fn,

        token_prob = naive_bayes.find_probability(fp,fn,number_of_articles,number_of_articles)
        token_probs.append(token_prob)

    #print token_probs
    return token_probs #and then you need to do abc / abc(a-1)(b-1)(c-1) to that result.
            

verdict(text,freq_pos,freq_neg)



